    private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
     
        
        if(pizza.isSelected()==true && small.isSelected()==true){
            item ="Pizza";
            isize ="Small";
            price = 200;
        }
        
        else if(pizza.isSelected()==true && medium.isSelected()==true){
            item ="Pizza";
            isize ="Medium";
            price = 350;
        }
        else if(pizza.isSelected()==true && large.isSelected()==true){
            item ="Pizza";
            isize ="Large";
            price = 500;
        }
        
        else if(burger.isSelected()==true && small.isSelected()==true){
            item ="Burger";
            isize ="Small";
            price = 150;
        }
        
        else if(burger.isSelected()==true && medium.isSelected()==true){
            item ="Burger";
            isize ="Medium";
            price = 200;
        }
        else if(burger.isSelected()==true && large.isSelected()==true){
            item ="Burger";
            isize ="Large";
            price = 300;
        }
        else if(sandwich.isSelected()==true && small.isSelected()==true){
            item ="Sandwich";
            isize ="Small";
            price = 150;
        }
        
        else if(sandwich.isSelected()==true && medium.isSelected()==true){
            item ="Sandwich";
            isize ="Medium";
            price = 200;
        }
        else if(sandwich.isSelected()==true && large.isSelected()==true){
            item ="Sandwich";
            isize ="Large";
            price = 250;
        }
        else if(icecream.isSelected()==true && small.isSelected()==true){
            item ="Ice Cream";
            isize ="Small";
            price = 50;
        }
        
        else if(icecream.isSelected()==true && medium.isSelected()==true){
            item ="Ice Cream";
            isize ="Medium";
            price = 100;
        }
        else if(icecream.isSelected()==true && large.isSelected()==true){
            item ="Ice Cream";
            isize ="Large";
            price = 200;
        }
        else if(coffee.isSelected()==true && small.isSelected()==true){
            item ="Coffee";
            isize ="Small";
            price = 50;
        }
        
        else if(coffee.isSelected()==true && medium.isSelected()==true){
            item ="Coffee";
            isize ="Medium";
            price = 100;
        }
        else if(coffee.isSelected()==true && large.isSelected()==true){
            item ="Coffee";
            isize ="Large";
            price = 200;
        }
        
        
        
        int qty = Integer.parseInt(txtqty.getValue().toString());
        int tot = qty*price;
        
        model = (DefaultTableModel)table.getModel();
        
        model.addRow(new Object[]
            {
                item,
                isize,
                price,
                qty,
                tot
            }
        );
        
        int sum = 0;
        for(int a = 0;a<table.getRowCount();a++){
            sum = sum+Integer.parseInt(table.getValueAt(a,4).toString());
        }
        txttotal.setText(Integer.toString(sum));
        
    }//GEN-LAST:event_addActionPerformed

