    private void printActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_printActionPerformed
         int num1 = Integer.parseInt(txttotal.getText());
         int num2 = Integer.parseInt(txtpayment.getText());
         int returncash = num2-num1;
         txtreturncash.setText(Integer.toString(returncash));
    }//GEN-LAST:event_printActionPerformed

